-- Table structure for v_coupon
-- ----------------------------
DROP TABLE IF EXISTS `v_coupon`;
CREATE TABLE `v_coupon` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `create_user_id` bigint(20) NOT NULL COMMENT '创建用户id',
  `merchant_id` bigint(20) NOT NULL COMMENT '商户id',
  `store_id` bigint(20) DEFAULT NULL COMMENT '店铺Id',
  `name` varchar(20) DEFAULT NULL COMMENT '优惠券名称',
  `type` smallint(5) NOT NULL DEFAULT '0' COMMENT '优惠券类型(0:优惠券;1:现金券)',
  `preferential_type` smallint(5) DEFAULT '0' COMMENT '优惠方式(0:指定金额,1:折扣)',
  `discount` smallint(5) DEFAULT '0' COMMENT '折扣(9折:90)',
  `origin_discount` smallint(5) DEFAULT '0' COMMENT '实际折扣(9折:90)',
  `alias` varchar(20) DEFAULT NULL COMMENT '别名',
  `value` decimal(6,2) DEFAULT '0.00' COMMENT '面值',
  `origin_value` smallint(5) DEFAULT '0' COMMENT '面值(单位:分)',
  `quota` smallint(5) DEFAULT '0' COMMENT '每人限领(单位:张)',
  `at_least` decimal(6,2) DEFAULT '0.00' COMMENT '使用门槛(单位:元)',
  `origin_at_least` smallint(5) DEFAULT '0' COMMENT '使用门槛(单位:分)',
  `is_at_least` bit(1) DEFAULT b'0' COMMENT '使用门槛限制',
  `total` smallint(5) DEFAULT '0' COMMENT '发放总量',
  `stock` smallint(5) DEFAULT '0' COMMENT '剩余张数',
  `start_at` datetime DEFAULT NULL COMMENT '生效时间',
  `start_time` bigint(20) DEFAULT NULL COMMENT '生效时间(long值)',
  `end_at` datetime DEFAULT NULL COMMENT '失效时间',
  `end_time` bigint(20) DEFAULT NULL COMMENT '失效时间(long值)',
  `expire_notice` bit(1) DEFAULT b'0' COMMENT '到期提醒',
  `description` varchar(100) DEFAULT NULL COMMENT '使用说明',
  `card_id` bigint(20) DEFAULT NULL COMMENT '可领取的会员等级',
  `is_sync_weixin` bit(1) DEFAULT b'0' COMMENT '同步至微信卡包',
  `is_invalid` bit(1) DEFAULT b'1' COMMENT '是否有效',
  `is_expired` bit(1) DEFAULT b'0' COMMENT '是否过期',
  `is_share` bit(1) DEFAULT b'0' COMMENT '是否允许分享领取链接',
  `url` varchar(100) DEFAULT NULL COMMENT '领取链接',
  `use_count` smallint(5) DEFAULT '0' COMMENT '使用数量',
  `get_count` smallint(5) DEFAULT '0' COMMENT '领取数量',
  `get_count_user` smallint(5) DEFAULT NULL COMMENT '领取人数',
  `use_condition` varchar(100) DEFAULT NULL COMMENT '使用条件描述',
  `weixin_title` varchar(50) DEFAULT NULL COMMENT '卡券标题',
  `weixin_sub_title` varchar(20) DEFAULT NULL COMMENT '卡券副标题',
  `service_phone` varchar(20) DEFAULT NULL COMMENT '客服电话',
  `color_id` bigint(20) DEFAULT NULL COMMENT '卡券颜色',
  `bg_color` varchar(10) DEFAULT NULL COMMENT '背景颜色GRB值',
  `can_give_friend` bit(1) DEFAULT b'0' COMMENT '可否赠送',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  `status` smallint(1) NOT NULL DEFAULT '1' COMMENT '状态',
