mapper.select:
select * from v_coupon where 1 = 1 and status = 1

    and name like CONCAT('%',#{name},'%')


    and is_expired = #{isExpired}


    and is_invalid = #{isInvalid}


    and store_id = #{storeId}


    and type = #{type}


    and merchant_id = #{merchantId}





                order by ${orderByField} ASC


                order by ${orderByField} DESC




        order by create_time DESC

mapper.update:
    	UPDATE v_coupon
		SET stock = stock - #{getCount},
		 get_count = get_count + #{getCount}
		WHERE
			id = #{couponId}
		AND stock >= #{getCount}

    	UPDATE v_coupon
		SET use_count = use_count + #{useCount}
		WHERE
			id = #{couponId}
		AND stock >= use_count + #{useCount}
