mapper.select:
SELECT cf.id AS id, cf.verify_code as verifyCode, m.sex AS sex, u.headimgurl AS headImg, u.nickname AS nickname, c.`value` AS VALUE, c.`name` AS couponName, c.type AS couponType, m.`name` AS memberName, m.mobile AS mobile, cf.create_time AS createTime, cf.is_used AS isUsed FROM v_coupon_fetch cf LEFT JOIN v_coupon c ON cf.coupon_id = c.id LEFT JOIN v_member m ON cf.member_id = m.id LEFT JOIN v_wx_user u ON m.wx_user_id = u.id WHERE cf.`status` = 1 AND c.`status` = 1 AND m.`status` = 1
and c.`name` like CONCAT('%',#{name},'%')
and c.`type` = #{type}
and m.`mobile` = #{mobile}
and cf.`is_used` = #{isUsed}
and cf.merchant_id = #{merchantId}
and u.nickname like CONCAT('%',#{nickname},'%')
and cf.verify_code like CONCAT('%',#{verifyCode},'%')
order by orderByField ASC
order by orderByField DESC
order by cf.create_time DESC

SELECT cf.id AS id, cf.verify_code As verifyCode, cf.coupon_id As couponId, c.`name` AS couponName, c.discount As discount, c.value As value, c.use_condition As useCondition, c.description As description, c.start_at As startAt, c.end_at As endAt, cf.is_invalid As isInvalid, cf.is_used As isUsed, c.is_invalid As couponIsInvalid, m.sex AS sex, u.headimgurl AS headImg, c.`value` AS VALUE, c.type AS couponType, c.preferential_type As preferentialType, m.`name` AS memberName, m.mobile AS mobile, cf.create_time AS createTime, cf.is_used AS isUsed FROM v_coupon_fetch cf LEFT JOIN v_coupon c ON cf.coupon_id = c.id LEFT JOIN v_member m ON cf.member_id = m.id LEFT JOIN v_wx_user u ON m.wx_user_id = u.id WHERE cf.`status` = 1 AND c.`status` = 1 AND m.`status` = 1
and cf.coupon_Id = #{couponId}
and cf.id = #{id}
