mapper.select:
SELECT ct.id AS id, ct.used_order_no as usedOrderNo, m.sex AS sex, u.headimgurl AS headImg, u.nickname AS nickname, c.`value` AS VALUE, c.`name` AS couponName, c.type AS couponType, m.`name` AS memberName, m.mobile AS mobile, ct.used_time AS usedTime, ct.used_order_no AS usedOrderNo, ct.used_value AS usedValue FROM v_coupon_trade ct LEFT JOIN v_coupon c ON ct.coupon_id = c.id LEFT JOIN v_member m ON ct.member_id = m.id LEFT JOIN v_wx_user u ON m.wx_user_id = u.id WHERE ct.`status` = 1 AND c.`status` = 1 AND m.`status` = 1
and c.`name` like CONCAT('%',#{name},'%')
and ct.merchant_id = #{merchantId}
and c.`type` = #{type}
and m.`mobile` = #{mobile}
and u.`nickname` like CONCAT('%',#{nickname},'%')
order by orderByField ASC
order by orderByField DESC
order by ct.create_time DESC
