mapper.select:
SELECT mc.card_number, mc.create_time, m.mobile, wu.headimgurl, wu.nickname, c.grant_type, mc.state, c.`name` as cardName FROM v_member_card mc LEFT JOIN v_member m ON mc.member_id = m.id LEFT JOIN v_card c ON mc.card_id = c.id LEFT JOIN v_wx_user wu ON m.wx_user_id = wu.id WHERE mc.`status` = 1 AND m.`status` = 1 AND c.`status` = 1 AND wu.`status` = 1
and mc.card_number like CONCAT('%',#{cardNumber},'%')
and c.id =#{cardId}
and mc.merchant_id = #{merchantId}
and c.grant_type = #{grantType}
and wu.nickname like CONCAT('%',#{nickname},'%')
and mc.state = #{state}
and m.mobile = #{mobile}
order by orderByField ASC
order by orderByField DESC
order by mc.create_time DESC
