mapper.select:
select m.*,wu.nickname , mc.card_number AS cardNumber from v_member m LEFT JOIN v_wx_user wu on wu.id=m.wx_user_id LEFT JOIN v_member_card mc on mc.member_id=m.id where 1 = 1 and m.status = 1 and mc.is_default=1
and m.name like CONCAT('%',#{memName},'%')
and m.mobile like CONCAT('%',#{memMobile},'%')
and wu.nickname like CONCAT('%',#{memNickName},'%')
and m.sex = #{memSex}
and mc.card_id =#{cardId}
and mc.card_number =#{cardNumber}
order by ${orderByField} ASC
order by ${orderByField} DESC
order by create_time DESC
