mapper.select:
SELECT pt.id, pt.create_time AS createTime, pt.in_out AS in_out, pt.point, pt.trade_type, wx.nickname, m.mobile, p.`name` AS pointName FROM v_point_trade pt LEFT JOIN v_point p ON pt.point_id = p.id LEFT JOIN v_member m ON m.id = pt.member_id LEFT JOIN v_wx_user wx ON wx.openid = m.open_id WHERE 1 = 1 AND pt.`status` = 1 AND m.`status` = 1 AND m.merchant_id = #{merchantId}
and wx.nickname like CONCAT('%',#{nickname},'%')
and m.mobile like CONCAT('%',#{mobile},'%')
and pt.in_out = #{inOut}
and pt.point_id = #{pointId}
and pt.trade_type = #{tradeType}
order by orderByField ASC
order by orderByField DESC
order by pt.create_time DESC
